mapper.update:
UPDATE qna SET
title = #{title},
content = #{content},
fnamemain = #{fnamemain}
WHERE qnano = #{qnano};

mapper.insert:
INSERT INTO qna (name,passwd,title,content,fnamemain,wdate,grpno)
VALUES
(#{name},#{passwd},#{title},#{content},#{fnamemain},sysdate(),(SELECT * FROM (SELECT ifnull(MAX(GRPNO), 0) + 1 FROM qna) q));
